import React from "react";

const SafetyTips = () => {
  return (
    <div>
      <h2>Safety Tips</h2>
      <ul>
        <li>Always share your live location with family.</li>
        <li>Save emergency contacts on your phone.</li>
        <li>Avoid isolated areas at night.</li>
        <li>Trust your instincts and act fast if you feel unsafe.</li>
      </ul>
    </div>
  );
};

export default SafetyTips;
