import React from 'react';
import Navbar from '../components/Navbar';

function SafetyTips() {
  return (
    <div>
      <Navbar />
      <div className="p-6">
        <h2 className="text-3xl font-bold text-red-600 mb-4 text-center">Safety Tips</h2>
        <ul className="list-disc pl-8 text-lg">
          <li>Stay aware of your surroundings.</li>
          <li>Avoid walking alone in isolated areas.</li>
          <li>Keep your emergency contacts updated.</li>
          <li>Use safety apps for tracking & alerts.</li>
          <li>Trust your instincts and seek help when needed.</li>
        </ul>
      </div>
    </div>
  );
}

export default SafetyTips;
